mapper.select:
SELECT C.cus_no , C.cus_nm , C.cus_phone , C.cus_chnnel , O.order_no , O.prod_nm , O.conn , O.con_date , date_format(O.ord_date,'%Y-%m-%d') as ord_date , date_format(O.reg_date,'%Y-%m-%d') as reg_date , O.reg_nm , O.upd_date , O.upd_nm , O.comp_yn , O.pay_md , O.pay_nt , O.ord_amt , O.ord_time FROM mu_tb_customer C INNER JOIN mu_tb_order O ON C.cus_no = O.cus_no WHERE 1=1
AND O.order_no = #{orderNo}
AND O.ord_date = #{ordDate}
AND C.cus_nm = #{cusNm}
AND C.cus_phone like CONCAT('%',#{cusPhone},'%')

mapper.insert:
INSERT INTO mu_tb_order(
	prod_nm
	, conn
	, con_date
	, ord_date
	, reg_date
	, reg_nm
	, upd_date
	, upd_nm
	, cus_no
	, comp_yn
	, pay_md
	, pay_nt
	, ord_amt
	, ord_time
)VALUES(
	#{prodNm}
	, #{conn}
	, now()
	, #{ordDate}
	, now()
	, '무릉방앗간'
	, now()
	, '무릉방앗간'
	, #{cusNo}
	, 'N'
	, #{payMd}
	, #{payNt}
	, #{ordAmt}
	, #{ordTime}
)

UPDATE mu_tb_order
SET
	prod_nm 	= #{prodNm}
	, conn   	= #{conn}
	, con_date	= #{conDate}
	, ord_date	= #{ordDate}
	, upd_date	= now()
	, upd_nm	= '무릉방앗간'
	, pay_md	= #{payMd}
	, pay_nt	= #{payNt}
	, ord_amt	= #{ordAmt}
	, ord_time	= #{ordTime}
WHERE order_no = #{orderNo}

mapper.update:
UPDATE mu_tb_order
SET comp_yn=#{compYn}
WHERE order_no=#{orderNo}
